#include "friendlyerrornames.h"
#include <QCoreApplication>

QString FriendlyErrorNames::getString(const QString& errorCode)
{
    return getErrorMap().value(errorCode, QString());
}

const QMap<QString, QString>& FriendlyErrorNames::getErrorMap()
{
    static QMap<QString, QString> errorMap = initializeErrorMap();
    return errorMap;
}

